mapper.select:
SELECT

FROM mail_site a

WHERE a.id = #{id}

SELECT

FROM mail_site a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.user_id = #{userId}


		AND a.label = #{label}


		AND a.title LIKE
		'%'||#{title}||'%'
		'%'+#{title}+'%'
		concat('%',#{title},'%')


		AND a.sender = #{sender}


		AND a.create_date = #{createDate}


		AND a.receiver = #{receiver}


		AND a.context = #{context}


		AND a.read_flag = #{readFlag}


		AND a.del_flag = #{delFlag}


		AND a.create_by = #{createBy.id}


		AND a.update_date = #{updateDate}


		AND a.update_by = #{updateBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM mail_site a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO mail_site( id, label, title, create_date, sender, receiver, context, create_by, update_date, update_by, read_flag, del_flag
) VALUES ( #{id}, #{label}, #{title}, #{createDate}, #{sender}, #{receiver}, #{context}, #{createBy.id}, #{updateDate}, #{updateBy.id}, #{readFlag}, #{delFlag}
)

mapper.update:
UPDATE mail_site SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
