mapper.select:
SELECT 

FROM member_account a, sys_user b

	a.del_flag = #{DEL_FLAG_NORMAL}
	and b.id = a.user_id

		AND a.account_type = #{accountType}



		AND a.qq_no = #{qqNo}


		AND a.mobile_no = #{mobileNo}


		AND a.status = #{status}


		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE member_account SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE member_account SET 	
	sec_password = #{newPassWord}
WHERE user_id = #{id}

UPDATE member_account_card SET 
	del_flag = "1"
WHERE bank_card_no = #{bankCardNo}

UPDATE member_account SET 
	blance = #{blance},
	mobile_no = #{mobileNo},
	qq_no  = #{qqNo}		  
WHERE 
	user_id = #{userId}

UPDATE sys_user SET 
	login_name = #{loginName},
	email = #{eMail}		  
WHERE 
	id = #{userId}
