mapper.select:
SELECT 

FROM lottery_time_num a

WHERE a.id = #{id}

SELECT 

FROM lottery_time_num a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.lottery_code = #{lotteryCode}


		AND a.lottery_issue_no = #{lotteryIssueNo}


		AND a.bet_start_gmt = #{betStartGmt}


		AND a.bet_end_gmt = #{betEndGmt}


		AND a.draw_num = #{drawNum}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lottery_time_num a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lottery_time_num SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
