mapper.select:
SELECT 

FROM lottery_type a

WHERE a.id = #{id}

SELECT
FROM lottery_type a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM lottery_type a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lottery_type SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
