mapper.select:
SELECT 

FROM lottery_order a

WHERE a.id = #{id}

SELECT 

FROM lottery_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.user_id = #{user.id}


		AND a.lottery_code = #{lotteryCode}


		AND a.bet_issue_no = #{betIssueNo}


		AND a.bet_type = #{betType}


		AND a.bet_detail = #{betDetail}


		AND a.bet_amount = #{betAmount}


		AND a.bet_rate = #{betRate}


		AND a.play_mode_money = #{playModeMoney}


		AND a.play_mode_commission_rate = #{playModeCommissionRate}


		AND a.play_mode_money_type = #{playModeMoneyType}


		AND a.order_source = #{orderSource}


		AND a.order_type = #{orderType}


		AND a.schema_id = #{schemaId}


		AND a.win_amount = #{winAmount}


		AND a.withdraw_amount = #{withdrawAmount}


		AND a.status = #{status}


		AND a.create_date = #{createDate}


		AND a.create_by = #{createBy.id}


		AND a.update_date = #{updateDate}


		AND a.update_by = #{updateBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lottery_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lottery_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
