mapper.select:
SELECT 

FROM company_card a

WHERE a.id = #{id}

SELECT 

FROM company_card a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.company_id = #{companyId}


		AND a.bank_card_no = #{bankCardNo}


		AND a.bank_card_holder = #{bankCardHolder}


		AND a.bank_branch_province = #{bankBranchProvince}


		AND a.bank_branch_city = #{bankBranchCity}


		AND a.bank_branch_name = #{bankBranchName}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM company_card a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE company_card SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
