mapper.select:
SELECT 
	
FROM contract a

WHERE a.id = #{id}

SELECT 
	
FROM contract a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		AND a.org_id LIKE 
			'%'||#{office.id}||'%'
			'%'+#{office.id}+'%'
			concat('%',#{office.id},'%')
	
	
		AND a.org_name LIKE 
			'%'||#{orgName}||'%'
			'%'+#{orgName}+'%'
			concat('%',#{orgName},'%')
	
	
		AND a.user_id = #{user.id}
	
	
		AND a.user_name LIKE 
			'%'||#{userName}||'%'
			'%'+#{userName}+'%'
			concat('%',#{userName},'%')
	
	
		AND a.account_id = #{accountId}
	
	
		AND a.open_type = #{openType}
	
	
		AND a.benefit_type = #{benefitType}
	
	
		AND a.benefit_cycle = #{benefitCycle}
	
	
		AND a.contract_time = #{contractTime}
	
	
		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

SELECT 
	
FROM contract a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
