mapper.select:
SELECT 
	
FROM todo_task a

WHERE a.id = #{id}

SELECT 
	
FROM todo_task a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')
	
	
		AND a.type = #{type}
	
	
		AND a.status = #{status}
	


	
		ORDER BY ${page.orderBy}

SELECT 
	
FROM todo_task a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}

mapper.update:
UPDATE todo_task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
